import React, { useEffect } from 'react';

import './App.css';

import 'react-chat-widget/lib/styles.css';
import { Widget, toggleWidget, addResponseMessage, addUserMessage , toggleMsgLoader } from 'react-chat-widget';


const handleNewUserMessage = (newMessage) => {
  console.log(`New message incoming! ${newMessage}`);
  // Now send the message throught the backend API
};

const sendANewMessageAsSupport = async (message) => { 
  toggleMsgLoader()
  await setTimeout(() => {
    addResponseMessage(message)
  }, 1500)
}

function App() {
  
  useEffect( async () => {
    await toggleWidget()
    await sendANewMessageAsSupport("Olá, seja bem-vindo(a) ao assistente virtual da Rio Ben!") 
    await sendANewMessageAsSupport("Estou aqui hoje para te apresentar um de nossos produtos, a proteção para vidros.")
  },[])

  return (
    <div className="App">
      <Widget 
        title="RioBen Proteção Veicular"
        subtitle="Você mais protegido"
        senderPlaceHolder="Escreva sua resposta aqui"
        fullScreenMode={true}
        showChat={true}
        autofocus={true}
        handleNewUserMessage={handleNewUserMessage}
      />
    </div>
  );
}

export default App;
