import React from 'react';
import { ThemeProvider } from 'styled-components';
import ChatBot from 'react-simple-chatbot';

//imagens
import promotional_banner from './assets/promotional-image.png'

// all available props
const theme = {
  background: '#6a99b2',
  fontFamily: 'Tahoma',
  headerBgColor: '#EF6C00',
  headerFontColor: '#fff',
  headerFontSize: '15px',
  botBubbleColor: '#EF6C00',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
};

const PromotionalBanner = () => { 
  return (
    <img src={promotional_banner} alt="Imagem promocional" style={{width: 540, height: 540}} />
  )
}

const steps = [
  {
    id: '1',
    message: 'Olá, meu nome é GMARIA e eu sou a atendente virtual da Rio Ben Proteção Veicular',
    trigger: '2'
  },
  {
    id: '2',
    message: 'Eu gostaria de lhe apresentar um de nossos benefícios: O carro reserva!',
    trigger: '3'
  },
  {
    id: '3',
    message: 'O carro reserva é um benefício um veículo sem custos adicionais para você não fique na mão enquanto seu veículo é consertado ou enquanto o seu veículo não seja recuperado em caso de roubo.',
    trigger: '4'
  },
  {
    id: '4',
    component: <PromotionalBanner />,
    trigger: '5'
  },
  {
    id: '5',
    message: 'Caso você tenha interesse em contratar esse plano, clique no botão abaixo.',
    trigger: '6'
  },
  {
    id: '6',
    options: [
      { value: 1, label: 'Quero contratar agora!', trigger: '3' },
      { value: 2, label: 'Não, obrigado', trigger: '2' },
    ],
    end: true
  },
];



const App = () => (
  <ThemeProvider theme={theme}>
    <ChatBot 
      steps={steps} 
      style={{width: '40vw',  marginLeft: '30vw', marginTop: '5vh'}}
      botDelay={3000}
    />
  </ThemeProvider>
);

export default App;
