import React, { useEffect } from 'react';

import './App.css';

import 'react-chat-widget/lib/styles.css';
import { Widget, toggleWidget, addResponseMessage, addUserMessage , toggleMsgLoader } from 'react-chat-widget';


const handleNewUserMessage = (newMessage) => {
  console.log(`New message incoming! ${newMessage}`);
  // Now send the message throught the backend API
};

const sendANewMessageAsSupport = message => { 
  addResponseMessage(message)
}

const initialMessage = async () => { 
  await setTimeout(  () => {
    addResponseMessage("Olá, seja bem-vindo(a) ao assistente virtual da Rio Ben!")
  }, 1000)
  
  await setTimeout(  () => {
    sendANewMessageAsSupport("Estou aqui hoje para te apresentar um de nossos produtos, a proteção para vidros.")
  }, 2500)

  await setTimeout(  () => { 
    sendANewMessageAsSupport("A proteção para vidros é um sistema de proteção que oferece cobertura completa ao para-brisas, farois e lanternas do seu veículo")
  },1500)
}

function App() {
  
  useEffect( async () => {
    await toggleWidget() 
    await toggleMsgLoader()   
    await initialMessage()
  },[])

  return (
    <div className="App">
      <Widget 
        title="RioBen Proteção Veicular"
        subtitle="Você mais protegido"
        senderPlaceHolder="Escreva sua resposta aqui"
        fullScreenMode={true}
        autofocus={true}
        handleNewUserMessage={handleNewUserMessage}
      />
    </div>
  );
}

export default App;
