import React, { useEffect } from 'react';

import './App.css';
import { ThemeProvider } from 'styled-components';
import ChatBot from '../../lib/index';

function App() {

  // all available props
  const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#EF6C00',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#EF6C00',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

  const steps = [
    {
      id: '1',
      message: 'Hello World',
      end: true,
    },
  ];

  return (
    <ThemeProvider theme={theme}>
      <ChatBot
        steps={[
          {
            id: 'hello-world',
            message: 'Hello World!',
            end: true,
          },
        ]}
      />
    </ThemeProvider>
  );
}

export default App;
