import React from 'react';
import { ThemeProvider } from 'styled-components';
import ChatBot from 'react-simple-chatbot';

// all available props
const theme = {
  background: '#f5f8fb',
  fontFamily: 'Helvetica Neue',
  headerBgColor: '#EF6C00',
  headerFontColor: '#fff',
  headerFontSize: '15px',
  botBubbleColor: '#EF6C00',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
  width: '500'
};

const MyButton = () => { 
  return(
    <button>Meu bot√£o teste</button>
  )
}

const steps = [
  {
    id: '1',
    component: <MyButton />,
    trigger: '2'
  },
  {
    id: '2',
    component: <MyButton />,
    trigger: '3'
  },
  {
    id: '3',
    message: 'meu teste',
    end: true
  },
];



const App = () => (
  <ThemeProvider theme={theme}>
    <ChatBot 
      steps={steps} 
      style={{width: '50vw', marginLeft: '25vw', marginTop: 150}}
    />
  </ThemeProvider>
);

export default App;
